import React from 'react';
import { useSpring, animated } from 'react-spring';
import MissionIcon from '../../Mission-icon.png';
import VisionIcon from '../../Vision-icon.png';
import ValueIcon from '../../Value-icon.png';

const HomePage = () => {
  const imageSrc = 'https://www.shutterstock.com/image-photo/master-plan-urban-landscape-design-260nw-1031613082.jpg';

  const fadeIn = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: { duration: 1000 },
  });

  return (
    <div className="homepage-container" style={{ background: 'skyblue', textAlign: 'center' }}>
      <h2>Our Services</h2>
      <animated.div style={fadeIn}>
        <div className="image-container" style={{ marginBottom: '20px' }}>
          <img src={imageSrc} alt="Service Image" className="service-image" />
        </div>
        <div className="icon-container">
          <div className="icon">
            <img src={MissionIcon} alt="Mission Icon" />
            <p>Our Mission<br/>To provide technically excellent and innovative solutions in urban planning, environment, development research, and geo design.</p>
          </div>
          <div className="icon">
            <img src={VisionIcon} alt="Vision Icon" />
            <p><br/>To be a leading hub in urban planning, geo solutions, environment, and development research.</p>
          </div>
          <div className="icon">
            <img src={ValueIcon} alt="Values Icon" />
            <p><br/>
              - Honesty & Integrity<br/>
              - Professionalism<br/>
              - Client Satisfaction<br/>
              - Prompt service delivery<br/>
              - Technical Excellence<br/>
              - Social responsibility
            </p>
          </div>
        </div>
      </animated.div>
    </div>
  );
};

export default HomePage;
