import React from 'react';
import { useTrail, animated } from 'react-spring';

const ServicePage = () => {
  const services = [
    // Services array...
  ];

  const trail = useTrail(services.length, {
    opacity: 1,
    transform: 'translateY(0)',
    from: { opacity: 0, transform: 'translateY(20px)' },
    delay: 200, // Add a delay for each service animation
  });

  return (
    <div className="service-container" style={{ background: 'skyblue', textAlign: 'center' }}>
      <h2>Our Services</h2>
      {trail.map((style, index) => (
        <animated.p
          key={index}
          style={{
            ...style,
            // Add additional styles for creative animation
            // Example: Make odd-indexed services rotate
            transform: style.transform.interpolate((t) => `${t} rotate(${index % 2 === 0 ? 0 : 15}deg)`),
          }}
        >
          {services[index]}
        </animated.p>
      ))}
    </div>
  );
};

export default ServicePage;
